-- token的key
redis.log(redis.LOG_WARNING, "-------------------------获取令牌开始")
local tokens_key = KEYS[1]
-- 时间戳的key
local timestamp_key = KEYS[2]
--redis.log(redis.LOG_WARNING, "tokens_key " .. tokens_key)

-- 往令牌桶里面放令牌的速率，一秒多少个
local rate = tonumber(ARGV[1])
-- 令牌桶的数量
local capacity = tonumber(ARGV[2])
-- 当前的时间戳
local now = tonumber(ARGV[3])
-- 请求令牌的数量
local requested = tonumber(ARGV[4])

-- 放满令牌桶是的时长
local fill_time = capacity / rate
-- redis 过期时间 这里为什么是放满令牌桶的两倍
-- 这个时间不能太长，加入太长10s，你第一秒把令牌拿完，后面9s，就会出现突刺现象
-- 突刺现象指的是在第9s时发起请求,在第11s再次发起请求,等于在2s内发起多次请求
local ttl = math.floor(fill_time * 2)

-- 获取令牌桶的数量，如果为空，将令牌桶容量赋值
local last_tokens = tonumber(redis.call("get", tokens_key))
if last_tokens == nil then
    last_tokens = capacity
end
redis.log(redis.LOG_WARNING, "-------------------------当前令牌桶数量为:" .. last_tokens)
-- 获取最后的更新时间戳，如果为空，设置为0
local last_refreshed = tonumber(redis.call("get", timestamp_key))
if last_refreshed == nil then
    last_refreshed = 0
end
redis.log(redis.LOG_WARNING, "-------------------------令牌上次更新时间为:" .. last_refreshed)
-- 计算出时间间隔
local delta = math.max(0, now - last_refreshed)
-- 该往令牌桶放令牌的数量,剩余的令牌数量
local filled_tokens = math.min(capacity, last_tokens + (delta * rate))
-- 看剩余的令牌是否能够获取到
local allowed = filled_tokens >= requested
local new_tokens = filled_tokens
-- 零代表false
local allowed_num = 0
-- 如果允许获取得到，计算出剩余的令牌数量，并标记可以获取
if allowed then
    new_tokens = filled_tokens - requested
    allowed_num = 1
end

-- 存到redis
redis.call("setex", tokens_key, ttl, new_tokens)
redis.call("setex", timestamp_key, ttl, now)
--  lua可以返回多个字段，java获取时用List获取
-- new_tokens:剩余token
redis.log(redis.LOG_WARNING, "-------------------------当前剩余令牌数量为:" .. new_tokens)
redis.log(redis.LOG_WARNING, "-------------------------获取令牌结束")
if new_tokens > 0 then
    return true
else
    return false
end
